<?php
use PHPMailer\PHPMailer\PHPMailer;
use PHPMailer\PHPMailer\Exception;
require 'vendor/autoload.php'; // PHPMailer etc.
header('Content-Type: application/json');
// === LOGGING FUNCTION ===
function logError(string $msg){
    // put the log dir outside webroot or protect it via server config
    file_put_contents(__DIR__.'/logs/errors.log', date('[Y-m-d H:i:s] ').$msg."\n", FILE_APPEND);
}
// === HONEYPOT ===
if (!empty($_POST['extra_info'])) {
    logError("Honeypot triggered");
    echo json_encode(['success'=>false,'error'=>'Spam detected']);
    exit;
}
// === TIME CHECK ===
$pageLoadTime = intval($_POST['pageLoadTime'] ?? 0);
if (time() - $pageLoadTime < 3) {
    logError("Too fast submission");
    echo json_encode(['success'=>false,'error'=>'Bot detected']);
    exit;
}
// === SANITIZE INPUT ===
function cleanText($text){ return htmlspecialchars(trim((string)$text), ENT_QUOTES, 'UTF-8'); }
$name    = cleanText($_POST['guestName'] ?? '');
$email   = filter_var($_POST['guestEmail'] ?? '', FILTER_VALIDATE_EMAIL);
$phone   = cleanText($_POST['guestPhone'] ?? '');
$package = cleanText($_POST['package'] ?? '');
$dateRaw = $_POST['eventDate'] ?? '';
$date    = $dateRaw ? date('F j, Y', strtotime($dateRaw)) : '';
$guests  = max(0,intval($_POST['guestCount'] ?? 0));
$notes   = cleanText($_POST['notes'] ?? '');
$addons  = !empty($_POST['addon']) ? implode(', ', array_map('cleanText', (array)$_POST['addon'])) : 'None';
$submittedAt = date('Y-m-d H:i:s');
// === VALIDATE ===
$errors = [];
if (!$name)  $errors[] = 'Name is required';
if (!$email) $errors[] = 'Valid email is required';
if (!$dateRaw)  $errors[] = 'Event date is required';
if ($guests < 1) $errors[] = 'Guests must be at least 1';
if($errors){
    logError("Validation errors: ".implode(', ',$errors));
    echo json_encode(['success'=>false,'error'=>implode('. ',$errors)]);
    exit;
}
// === SANITIZE FILENAME for ICS ===
$filenameSafe = preg_replace('/[^a-zA-Z0-9_\-]/','_', $name);
$icsPath = sys_get_temp_dir() . "/reservation_{$filenameSafe}.ics";
// === ICS GENERATION ===
$startDate = date('Ymd', strtotime($dateRaw));
$icsDescription = "Guests: $guests, Package: $package, Add-ons: $addons, Notes: $notes";
$icsContent = <<<ICS
BEGIN:VCALENDAR
VERSION:2.0
PRODID:-//Aria Amore//Reservation//EN
BEGIN:VEVENT
SUMMARY:Reservation for $name
DTSTART;VALUE=DATE:$startDate
DTEND;VALUE=DATE:$startDate
DESCRIPTION:$icsDescription
END:VEVENT
END:VCALENDAR
ICS;
file_put_contents($icsPath,$icsContent);
// === MAIL SENDING FUNCTION (uses env vars if present) ===
function sendMail($to, $toName, $subject, $htmlBody, $attachments = []) {
    $mail = new PHPMailer(true);
    try {
        $mail->isSMTP();
        $mail->Host = getenv('SMTP_HOST') ?: 'mail.ariaamore.com';
        $mail->SMTPAuth = true;
        $mail->Username = getenv('SMTP_USER') ?: 'no-reply@ariaamore.com';
        $mail->Password = getenv('SMTP_PASS') ?: 'Elloree25!';
        $smtpSecure = getenv('SMTP_SECURE') ?: 'ssl';
        if (strtolower($smtpSecure) === 'tls') {
            $mail->SMTPSecure = PHPMailer::ENCRYPTION_STARTTLS;
        } else {
            $mail->SMTPSecure = PHPMailer::ENCRYPTION_SMTPS;
        }
        $mail->Port = intval(getenv('SMTP_PORT') ?: 465);
        $fromEmail = getenv('FROM_EMAIL') ?: 'no-reply@ariaamore.com';
        $fromName  = getenv('FROM_NAME') ?: 'Aria Amore';
        $mail->setFrom($fromEmail, $fromName);
        $mail->addAddress($to, $toName);
        $mail->isHTML(true);
        $mail->Subject = $subject;
        $mail->Body = $htmlBody;
        $mail->AltBody = strip_tags(preg_replace('/\s+/', ' ', $htmlBody));
        foreach ($attachments as $file) {
            if (file_exists($file)) $mail->addAttachment($file);
        }
        return $mail->send();
    } catch (Exception $e) {
        logError("Mail error sending to {$to}: {$mail->ErrorInfo}");
        return false;
    }
}
